var scrollAnc = function scrollAnc($object, $speed, $scroll) {
  var hash = $object;
  var $target;
  var t_hash; // let h_h = document.querySelector(".header").clientHeight;
  // console.log($scroll);

  if (is_open) $menuBtn.dispatchEvent(new Event("click"));

  if (hash === "#") {
    t_hash = 0;
  } else {
    t_hash = $scroll;
  }

  TweenMax.to(window, $speed, {
    scrollTo: t_hash
  });
};
