window.addEventListener('scroll', function (e) {
  e.preventDefault();
  _sTop = document.body.scrollTop || document.documentElement.scrollTop;
  wh = window.innerHeight;

  var _sMdl = _sTop + wh / 2;

  var _sBtm = _sTop + wh;

  var diffVal = 100;
  /* slideIn */

  for (var i = 0; i < slideInArr.length; i++) {
    if (!slideInFlagArr[i] && slideInTopArr[i] <= _sBtm - diffVal) {
      slideInFlagArr[i] = true;
      slideInAnime(slideInArr[i]);
    }
  }
  /* staggerSlide */


  for (var _i3 = 0; _i3 < staggerSlideArr.length; _i3++) {
    if (!staggerSlideFlagArr[_i3] && staggerSlideTopArr[_i3] <= _sBtm - diffVal) {
      staggerSlideFlagArr[_i3] = true;
      staggerSlideInAnime(staggerSlideArr[_i3]);
    }
  }
});
